import { Button } from "antd";
import {
  collection,
  deleteDoc,
  doc,
  getDocs,
  onSnapshot,
  query,
} from "firebase/firestore";
import moment from "moment";
import { useEffect, useState } from "react";
import { db } from "./firebase";

function ListCheckIn() {
  const [listAttend, setListAttend] = useState([]);
  const [total, setTotal] = useState({
    totalJoin: 0,
    total: 0,
  });
  const [listCountUnit, setListCountUnit] = useState([]);
  const [tabs, setTabs] = useState([]);
  const [unit, setUnit] = useState("All");
  const [isJoin, setIsJoin] = useState(true);

  useEffect(() => {
    const q = query(collection(db, "diemdanh"));
    const unsubscribe = onSnapshot(q, async (querySnapshot) => {
      const arr = querySnapshot.docs.map((d) => d.data());
      const querySnapshot_2 = await getDocs(collection(db, "daibieu"));
      const roomUnit = Array.from(
        new Set(querySnapshot_2.docs.map((e) => e.data().room))
      );
      roomUnit.sort((a, b) => Number(a) - Number(b));
      setTabs(roomUnit);

      const filteredUsers = isJoin
        ? arr.filter((n) => (unit === "All" ? true : n.room === unit))
        : querySnapshot_2.docs
          .map((doc) => doc.data())
          .filter((n) => !arr.find((r) => r.qrcode === n.qrcode) && (unit === "All" ? true : n.room === unit));

      setListAttend(filteredUsers);

      const newListCountUnit = roomUnit.map((key) => ({
        key,
        value: {
          data: arr.filter((u) => u.room === key).length,
          totalItemsUnit: querySnapshot_2.docs
            .map((d) => d.data())
            .filter((c) => c.soban === key).length,
        },
      }));

      setListCountUnit(newListCountUnit);
      setTotal({
        totalJoin: arr.length,
        total: querySnapshot_2.docs.length,
      });
    });

    return () => {
      unsubscribe();
    };
  }, [isJoin, unit]);

  return (
    <div className="main-wrapper-thong-ke" style={{ fontFamily: `"Montserrat", sans-serif` }}>
      <div className="h-100" style={{ paddingLeft: "10vw", paddingRight: "10vw", paddingTop: "31.5vh" }}>
        <div className="text-center" style={{ marginBottom: "7.5vh" }}>
          {/* Image or title can be added here */}
        </div>
        <div className="d-flex">
          <div className="flex-1" style={{ paddingRight: "20px" }}>
            <div className="title-wrapper" style={{ marginBottom: 0 }}>
              <div style={{ alignItems: "center" }} className="d-flex">
                <select
                  className="form-select form-select-sm w-100"
                  value={unit}
                  onChange={(e) => setUnit(e.target.value)}
                >
                  <option value="All">Tất cả</option>
                  {tabs.map((t) => (
                    <option value={t} key={t}>
                      Hội nghị {t}
                    </option>
                  ))}
                </select>
                <div className="d-flex ms-2">
                  <Button
                    onClick={() => setIsJoin(true)}
                    type={isJoin ? "primary" : "dashed"}
                    className="me-2"
                  >
                    Đã tham gia
                  </Button>
                  <Button
                    onClick={() => setIsJoin(false)}
                    type={!isJoin ? "primary" : "dashed"}
                  >
                    Chưa tham gia
                  </Button>
                </div>
              </div>
            </div>
            <div className="w-100" style={{ maxHeight: "50.25vh", overflowY: "auto", marginTop: "3vh" }}>
              <table className="w-100" style={{ marginBottom: 0, marginTop: 0 }}>
                <thead>
                  <tr>
                    <th scope="col">STT</th>
                    <th scope="col">HỌ VÀ TÊN</th>
                    <th scope="col">ĐƠN VỊ</th>
                    <th scope="col">SỐ ĐIỆN THOẠI</th>
                    <th scope="col">EMAIL</th>
                    <th scope="col">ĐIỂM DANH</th>
                  </tr>
                </thead>
                <tbody>
                  {listAttend.map((l, i) => (
                    <tr key={i}>
                      <td>{i + 1}</td>
                      <td>{l.hovaten}</td>
                      <td>{l.donvi}</td>
                      <td>{l.sodienthoai}</td>
                      <td>{l.email}</td>
                      <td>
                        {l.checkIn
                          ? moment(l.checkIn).format("HH:mm DD-MM-YYYY")
                          : "Chưa Điểm danh"}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
          <div className="statistical">
            <div className="d-flex h-100" style={{ paddingLeft: 20, fontSize: 18 }}>
              <div>
                <p style={{ marginBottom: 5, background: "red", color: "white", padding: "7px 12px" }}>
                  Số lượng đại biểu đã tham gia:
                  <b className="ms-1">{total.totalJoin} / {total.total}</b>
                </p>

                <div className="listCountUnit">
                  {listCountUnit.map((l, i) => (
                    <p key={i} style={{ background: "#e8eeef", marginBottom: 3, padding: "10px 12px", textAlign: "center" }}>
                      {"Hội nghị "} {l.key} :{" "}
                      <b className="ms-1">{l.value.data} / {l.value.totalItemsUnit}</b>
                    </p>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ListCheckIn;
