import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import { doc, setDoc, collection, getDocs, query, orderBy } from 'firebase/firestore';
import { db } from '../firebase';
import 'react-toastify/dist/ReactToastify.css';

const initState = {
    qrcode: "",
    hovaten: "",
    donvi: "",
    sodienthoai: "",
    email: "",
    room: "",
};

const DangKy = () => {
    const [form, setForm] = useState(initState);
    const [rooms, setRooms] = useState([]);

    useEffect(() => {
        const fetchRooms = async () => {
            try {
                const roomsCollection = collection(db, "room");
                const roomsQuery = query(roomsCollection, orderBy("name")); // Sắp xếp theo trường "name"
                const roomSnapshot = await getDocs(roomsQuery);
                const roomList = roomSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
                setRooms(roomList);
            } catch (error) {
                toast.error("Không thể tải danh sách phòng!", {
                    position: "top-right",
                    autoClose: 3000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            }
        };

        fetchRooms();
    }, []);

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const usersRef = doc(db, "daibieu", uuidv4());

        if (!form.hovaten || form.hovaten === "") {
            form.hovaten = "Họ và tên Đại biểu";
        }

        for (let [key, val] of Object.entries(form)) {
            if (!val || val === "") {
                delete form[key];
            }
        }

        try {
            await setDoc(usersRef, form);
            setForm(initState);
            toast.success("Thêm mới thành công!", {
                position: "top-right",
                autoClose: 3000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        } catch (error) {
            toast.error("Có lỗi xảy ra. Vui lòng thử lại!", {
                position: "top-right",
                autoClose: 3000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
            <h1>ABC</h1>
            <form onSubmit={handleSubmit} style={{ maxWidth: '400px', width: '100%' }}>
                <div className="mb-3">
                    <label className="form-label">Họ và tên</label>
                    <input
                        className="form-control"
                        type="text"
                        placeholder="Họ và tên"
                        onChange={handleChange}
                        name="hovaten"
                        value={form.hovaten}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Đơn vị</label>
                    <input
                        className="form-control"
                        type="text"
                        required
                        onChange={handleChange}
                        name="donvi"
                        placeholder="Đơn vị công tác hay học tập"
                        value={form.donvi}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Số điện thoại</label>
                    <input
                        className="form-control"
                        type="text"
                        required
                        onChange={handleChange}
                        name="sodienthoai"
                        placeholder="Số điện thoại"
                        value={form.sodienthoai}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                        className="form-control"
                        type="text"
                        required
                        onChange={handleChange}
                        name="email"
                        placeholder="Email"
                        value={form.email}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Đăng ký</label>
                    <select
                        className="form-control"
                        name="room"
                        value={form.room}
                        onChange={handleChange}
                    >
                        <option value="">Chọn Hội thảo</option>
                        {rooms.map((room) => (
                            <option key={room.id} value={room.name}>
                                {room.name}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Đăng ký</button>
            </form>
        </div>
    );
};

export default DangKy;
