import React, { memo } from "react";
import titlecheckin from "../assets/checkin.png";
// import soban from "../assets/soban.png";

const ChaoMungComponent = ({ userCurrent }) => {
  return (
    <div
      className="chao-mung-container"
      style={{
        backgroundImage: `url(${titlecheckin})`, // Thay bằng hình nền mong muốn
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        width: '100%',
        height: '100vh', // Hoặc kích thước bạn muốn
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
      }}
    >
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "2.5vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.nguoidaidien || "."}
      </h2>
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "1.75vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.chucvu} {userCurrent?.tencongty || "."}
      </h2>
      <div style={{ maxWidth: "37.5%", marginTop: "1vh" }}>
        <img
          src={titlecheckin}
          style={{ width: "100%" }}
          alt="Welcome to the Check-in"
        />
      </div>

      {/* Uncomment if needed */}
      {/* <div style={{ marginTop: "2vh" }}>
                <div className="m-auto" style={{ maxWidth: "40%", position: "relative" }}>
                    <img className="w-100" src={soban} alt="Table" />
                    <h1
                        style={{
                            position: "absolute",
                            right: "3vw",
                            bottom: 0,
                            top: "33%",
                            transform: "translateY(-50%)",
                            marginBottom: 0,
                            fontWeight: 900,
                            fontSize: "5vw",
                        }}
                    >
                        {userCurrent?.soban}
                    </h1>
                </div>
            </div> */}
    </div>
  );
};

export default memo(ChaoMungComponent);
