import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // For navigation

function App() {
  const [showQuestion, setShowQuestion] = useState(true); // State to control the question visibility
  const history = useHistory(); // For navigation

  const handleResponse = (response) => {
    setShowQuestion(false); // Hide the question
    if (response === "yes") {
      history.push("/danhsach"); // Navigate to danh sách
    } else {
      history.push("/dangky"); // Navigate to đăng ký
    }
  };

  return (
    <div className="blank-page">
      {showQuestion ? (
        <>
          <h1>Chào mừng bạn đến với trang web!</h1>
          <p>Bạn đã đăng ký chưa?</p>
          <button onClick={() => handleResponse("yes")}>Có</button>
          <button onClick={() => handleResponse("no")}>Chưa</button>
        </>
      ) : (
        <h2>Đang chuyển hướng...</h2>
      )}

      <style jsx>{`
        .blank-page {
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          height: 100vh;
          text-align: center;
        }

        h1 {
          font-size: 2.5rem;
          margin-bottom: 20px;
        }

        p {
          font-size: 1.2rem;
          margin-bottom: 20px;
        }

        button {
          margin: 5px;
          padding: 10px 20px;
          border: none;
          border-radius: 5px;
          background-color: #007bff;
          color: white;
          cursor: pointer;
        }

        button:hover {
          background-color: #0056b3;
        }
      `}</style>
    </div>
  );
}

export default App;
