import React, { memo } from "react";
import nencheckin from "../assets/nencheckin.png";
import titlecheckin from "../assets/titlecheckin.png";
const ChaoMungComponent = ({ userCurrent }) => {
  return (
    <div
      className="chao-mung-container"
      style={{
        backgroundImage: `url(${nencheckin})`, // Thay bằng hình nền mong muốn
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        width: '100%',
        height: '100vh', // Hoặc kích thước bạn muốn
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
      }}
    ><div style={{ maxWidth: "37.5%", marginTop: "1vh" }}>
        <img
          src={titlecheckin}
          style={{ width: "100%" }}
          alt="Welcome to the Check-in"
        />
      </div>
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "2.5vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.nguoidaidien || "."}
      </h2>
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "1.75vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.chucvu} {userCurrent?.tencongty || "."}
      </h2>
      <div style={{ maxWidth: "37.5%", marginTop: "1vh" }}>
        <img
          src={titlecheckin}
          style={{ width: "100%" }}
          alt="Welcome to the Check-in"
        />
      </div>
    </div>
  );
};

export default memo(ChaoMungComponent);
