import { Modal } from "antd";
import { doc, setDoc, collection, getDocs } from "firebase/firestore";
import React, { memo, useEffect, useState } from "react";
import { toast } from "react-toastify";
import { db } from "../firebase";

const initState = {
  qrcode: "",
  nguoidaidien: "Người đại diện",
  soban: "",
  somayman: "",
  tencongty: "",
  chucvu: "",
  sokhachdikem: 0,
  room: "", // Thêm trường room
};

const UpdateUserModal = ({ modalIsOpen, setIsOpen, initForm }) => {
  const [form, setForm] = useState(initState);
  const [rooms, setRooms] = useState([]); // State để lưu danh sách phòng

  // Hàm xử lý thay đổi input
  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  // Hàm xử lý gửi form
  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedForm = { ...form };
    for (let [key, val] of Object.entries(updatedForm)) {
      if (!val) {
        delete updatedForm[key];
      }
    }

    const usersRef = doc(db, "users", form.id);

    try {
      await setDoc(usersRef, updatedForm);
      toast.success("Cập nhật thành công!", {
        position: "top-right",
        autoClose: 3000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
      });
    } catch (error) {
      toast.error("Cập nhật thất bại!", {
        position: "top-right",
      });
    } finally {
      setIsOpen(false);
      setForm(initState);
    }
  };

  // Lấy danh sách phòng từ Firestore
  const fetchRooms = async () => {
    const roomsCollection = collection(db, "rooms");
    const roomSnapshot = await getDocs(roomsCollection);
    const roomList = roomSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setRooms(roomList);
  };

  // Cập nhật form khi có dữ liệu khởi tạo
  useEffect(() => {
    if (initForm) {
      setForm(initForm);
    }
    fetchRooms(); // Gọi hàm lấy dữ liệu phòng khi modal mở
  }, [initForm]);

  return (
    <Modal
      title="Cập nhật thông tin đại biểu"
      centered
      open={modalIsOpen}
      onCancel={() => {
        setForm(initState);
        setIsOpen(false);
      }}
      footer={null}
    >
      <form onSubmit={handleSubmit}>
        {Object.keys(initState).map((key) => (
          <div key={key}>
            <p className="mb-0 mt-2">{key === "nguoidaidien" ? "Người đại diện" : key}</p>
            {key === "room" ? ( // Thêm trường select cho room
              <select
                className="form-control"
                name="room"
                value={form.room || ""} // Đảm bảo giá trị mặc định là string rỗng
                onChange={handleChange}
                required
              >
                <option value="">Chọn phòng</option> {/* Hiển thị thông báo "Chọn phòng" */}
                {rooms.map(room => (
                  <option key={room.id} value={room.name}>
                    {room.name}
                  </option>
                ))}
              </select>
            ) : (
              <input
                className="form-control"
                type={key === "sokhachdikem" ? "number" : "text"}
                placeholder={key === "nguoidaidien" ? "Người đại diện" : ""}
                onChange={handleChange}
                name={key}
                value={form[key]}
                required={key !== "somayman"}
              />
            )}
          </div>
        ))}
        <div
          className="d-flex align-items-center"
          style={{
            marginTop: "1rem",
            justifyContent: "flex-end",
          }}
        >
          <button type="submit" className="btn btn-primary text-white">
            Lưu
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default memo(UpdateUserModal);
