import React, { memo } from "react";
import titlecheckin from "../assets/titlecheckin.png";

const ChaoMungComponent = ({ userCurrent }) => {
  return (
    <div
      className="chao-mung-container"
      style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        width: '100%',
        height: '100vh', // Full viewport height
        backgroundColor: '#f0f0f0', // Optional: add a solid background color
      }}
    >
      <div style={{ maxWidth: "37.5%", marginBottom: "2vh" }}>
        <img
          src={titlecheckin}
          style={{ width: "100%" }}
          alt="Welcome to the Check-in"
        />
      </div>
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "2.5vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.nguoidaidien || "."}
      </h2>
      <h2
        className="font-large text-blue"
        style={{
          textTransform: "uppercase",
          marginBottom: "1.15vh",
          fontSize: "1.75vw",
          lineHeight: 1.2,
        }}
      >
        {userCurrent?.chucvu} {userCurrent?.tencongty || "."}
      </h2>
    </div>
  );
};

export default memo(ChaoMungComponent);
