import { Spin } from "antd";
import {
  collection,
  deleteDoc,
  doc,
  onSnapshot,
  orderBy,
  query,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import AdminWrappter from "./components/AdminWrappter";
import CoCauGiaiThuongModal from "./components/CoCauGiaiThuongModal";
import CreateUserModal from "./components/CreateUserModalx";
import UpdateUserModal from "./components/UpdateUserModal";
import { db } from "./firebase";
import useDebounce from "./hooks/useDebounce";

const UserMage = () => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [searchSoDienThoai, setSearchSoDienThoai] = useState("");
  const [selectUser, setSelectedUser] = useState(null);
  const [showModalCreate, setShowModalCreate] = useState(false);
  const [showCoCauModal, setCoCauModal] = useState(false);
  const [tabs, setTabs] = useState([]);
  const debouncedValue = useDebounce(search, 600);
  const debouncedSoDienThoai = useDebounce(searchSoDienThoai, 600);
  const [unit, setUnit] = useState("All");
  const [loading, setLoading] = useState(true);

  const handleModalUpdate = (val) => {
    if (!val) {
      setSelectedUser(null);
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Bạn có chắc chắn muốn xóa đại biểu này ?")) {
      const usersRef = doc(db, "daibieu", id);
      deleteDoc(usersRef);
    }
  };

  useEffect(() => {
    if (!unit) return;
    const q = query(collection(db, "daibieu"), orderBy("hovaten"));
    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      let arr = [];
      querySnapshot.forEach((doc) => {
        arr.push({ ...doc.data(), id: doc.id });
      });

      const roomUnit = Array.from(new Set(arr.map((e) => e.room)));
      roomUnit.sort((a, b) => Number(a) - Number(b));
      setTabs(roomUnit);

      setLoading(false);

      setUsers(
        arr.filter(
          (u) =>
            (unit === "All" ? true : u.room === unit) &&
            u.hovaten
              .toUpperCase()
              .includes(debouncedValue.toUpperCase()) &&
            u.hovaten.toUpperCase().includes(debouncedSoDienThoai.toUpperCase())
        )
      );
    });
    return () => {
      unsubscribe();
    };
  }, [debouncedValue, debouncedSoDienThoai, unit]);

  return (
    <>
      {loading && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            zIndex: 999,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            background: "rgba(0,0,0,.3)",
          }}
        >
          <Spin size="large" />
        </div>
      )}
      <ToastContainer />
      <AdminWrappter>
        <div
          className="text-center"
          style={{
            marginTop: "22vh",
          }}
        >
          {/* <img
            style={{
              maxWidth: "35%",
            }}
            src={titleImg}
            alt=""
          /> */}
        </div>
        <div
          style={{
            textAlign: "center",
            marginTop: "1.5rem",
          }}
        >
          <button
            className="btn text-danger btn-warning text-bold me-4"
            onClick={() => setCoCauModal(true)}
          >
            Cơ cấu giải thưởng
          </button>

          <button
            className="btn text-danger btn-warning text-bold"
            onClick={() => setShowModalCreate(true)}
          >
            Thêm mới khách hàng
          </button>
        </div>

        <div
          style={{
            marginTop: "1rem",
          }}
        >
          <div
            className="d-flex align-items-center justify-content-center"
            style={{
              marginBottom: "1vh",
            }}
          >
            {/* <div
              style={{
                maxWidth: "75%",
              }}
            > */}
            <input
              className="form-control form-control-sm"
              placeholder="Tìm kiếm đại biểu ( có dấu )"
              type="text"
              style={{
                maxWidth: "75%",
              }}
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>
          <div
            className="d-flex align-items-center justify-content-center"
            style={{
              marginBottom: "1vh",
            }}
          >
            <input
              className="form-control form-control-sm"
              placeholder="Tìm kiếm theo số điện thoại"
              type="text"
              style={{
                maxWidth: "75%",
              }}
              value={searchSoDienThoai}
              onChange={(e) => setSearchSoDienThoai(e.target.value)}
            />
            {/* </div> */}
          </div>
          <div
            className="d-flex align-items-center justify-content-center"
            style={{
              marginBottom: "3vh",
            }}
          >
            <select
              className="form-select form-select-sm"
              value={unit}
              style={{
                maxWidth: "75%",
              }}
              onChange={(e) => {
                setUnit(e.target.value);
              }}
            >
              <option value="All">Chọn Hội thảo</option>
              {tabs.map((t) => (
                <option value={t} key={t}>{t}
                </option>
              ))}
            </select>
          </div>
          <div
            style={{
              maxHeight: "42.25vh",
              overflowY: "auto",
            }}
          >
            <table className="w-100">
              <thead>
                <tr>
                  <th scope="col">STT</th>
                  <th scope="col">Người đại diện</th>
                  <th scope="col">Đơn vị</th>
                  <th scope="col">Số điện thoại</th>
                  <th scope="col">Đăng ký</th>
                  <th scope="col" colSpan={2}>Hành động</th>
                </tr>
              </thead>

              <tbody>
                {users.map((l, i) => (
                  <tr key={l.id}>
                    <td>{i + 1}</td>
                    <td>{l.hovaten}</td>
                    <td>{l.donvi}</td>
                    <td>{l.sodienthoai} </td>
                    <td>{l.room} </td>
                    <td>
                      <button
                        onClick={() => handleDelete(l.id)}
                        className="btn btn-danger me-2 text-white"
                      >
                        Xóa
                      </button>
                      <button
                        className="btn  btn-secondary"
                        onClick={() => setSelectedUser(l)}
                      >
                        Sửa
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </AdminWrappter>
      {/* modal  */}

      <CreateUserModal
        modalIsOpen={showModalCreate}
        setIsOpen={setShowModalCreate}
      />

      <UpdateUserModal
        modalIsOpen={Boolean(selectUser)}
        setIsOpen={handleModalUpdate}
        initForm={selectUser}
      />

      <CoCauGiaiThuongModal
        modalIsOpen={showCoCauModal}
        setIsOpen={setCoCauModal}
      />
    </>
  );
};

export default UserMage;
