import { Spin } from "antd";
import {
    collection,
    onSnapshot,
    orderBy,
    query,
} from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import AdminWrappter from "./components/AdminWrappter";
import UpdateUserModal from "./components/UpdateUserModal";
import { db } from "./firebase";
import useDebounce from "./hooks/useDebounce";
import titlehoinghi from "./assets/titlehoinghi.png";
import titledadangky from "./assets/titledadangky.png";

const DiemDanh = () => {
    const [users, setUsers] = useState([]);
    const [search, setSearch] = useState("");
    const [searchCongTy, setSearchCongTy] = useState("");
    const [selectUser, setSelectedUser] = useState(null);
    const [tabs, setTabs] = useState([]);
    const debouncedValue = useDebounce(search, 600);
    const debouncedTenCongTy = useDebounce(searchCongTy, 600);
    const [unit, setUnit] = useState("All");
    const [loading, setLoading] = useState(true);

    const handleModalUpdate = (val) => {
        if (!val) {
            setSelectedUser(null);
        }
    };

    useEffect(() => {
        if (!unit) return;

        const q = query(collection(db, "users"), orderBy("qrcode"));
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const arr = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));

            const banUnit = Array.from(new Set(arr.map((e) => e.soban)));
            banUnit.sort((a, b) => Number(a) - Number(b));
            setTabs(banUnit);
            setLoading(false);

            // Cập nhật danh sách người dùng theo điều kiện tìm kiếm
            const filteredUsers = arr.filter((u) =>
                (unit === "All" ? true : u.soban === unit) &&
                u.nguoidaidien.toUpperCase().includes(debouncedValue.toUpperCase()) &&
                u.tencongty.toUpperCase().includes(debouncedTenCongTy.toUpperCase())
            );

            setUsers(filteredUsers);
        }, (error) => {
            console.error("Error fetching users: ", error);
            setLoading(false); // Ensure loading is stopped on error
        });

        return () => {
            unsubscribe();
        };
    }, [debouncedValue, debouncedTenCongTy, unit]);

    return (
        <>
            {loading && (
                <div
                    style={{
                        position: "fixed",
                        inset: 0,
                        zIndex: 999,
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        background: "rgba(0,0,0,.3)",
                    }}
                >
                    <Spin size="large" />
                </div>
            )}
            <ToastContainer />
            <AdminWrappter>
                <div className="text-center">
                    <img
                        src={titlehoinghi}
                        style={{
                            width: "90%",
                            marginTop: "1vh",
                            marginBottom: "1vh"
                        }}
                        alt="Title Hôi Nghị"
                    />
                    <img
                        src={titledadangky}
                        style={{
                            width: "80%",
                            marginTop: "1vh",
                            marginBottom: "1vh"
                        }}
                        alt="Title Đăng Ký"
                    />
                    <div className="d-flex align-items-center justify-content-center" style={{ margin: "1vh auto" }}>
                        <input
                            className="form-control form-control-sm"
                            placeholder="Tìm kiếm theo họ và tên (có dấu)"
                            type="text"
                            style={{ maxWidth: "75%" }}
                            value={search}
                            onChange={(e) => setSearch(e.target.value)}
                        />
                    </div>
                    <div className="d-flex align-items-center justify-content-center" style={{ margin: "1vh auto" }}>
                        <input
                            className="form-control form-control-sm"
                            placeholder="Tìm kiếm theo số điện thoại"
                            type="text"
                            style={{ maxWidth: "75%" }}
                            value={searchCongTy}
                            onChange={(e) => setSearchCongTy(e.target.value)}
                        />
                    </div>

                    <div style={{ maxHeight: "42.25vh", overflowY: "auto", margin: "0 auto", textAlign: "center" }}>
                        <table className="w-100" style={{ margin: "0 auto" }}>
                            <thead>
                                <tr>
                                    <th scope="col">STT</th>
                                    <th scope="col">HỌ VÀ TÊN</th>
                                    <th scope="col">ĐƠN VỊ</th>
                                    <th scope="col">SỐ ĐIỆN THOẠI</th>
                                    <th scope="col">ĐĂNG KÝ</th>
                                    <th scope="col">HÀNH ĐỘNG</th>
                                </tr>
                            </thead>
                            <tbody>
                                {users.length > 0 ? (
                                    users.map((l, i) => (
                                        <tr key={l.id}>
                                            <td>{i + 1}</td>
                                            <td>{l.nguoidaidien}</td>
                                            <td>{l.tencongty}</td>
                                            <td>{l.sodienthoai}</td>
                                            <td>{l.room}</td>
                                            <td>
                                                <button
                                                    className="btn btn-warning"
                                                    onClick={() => setSelectedUser(l)}
                                                >
                                                    Sửa
                                                </button>
                                                <button
                                                    className="btn btn-success"
                                                    onClick={() => setSelectedUser(l)}
                                                >
                                                    Xác nhận
                                                </button>
                                            </td>
                                        </tr>
                                    ))
                                ) : (
                                    <tr>
                                        <td colSpan="6">Không có giá trị.</td>
                                    </tr>
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </AdminWrappter>
            <UpdateUserModal
                modalIsOpen={Boolean(selectUser)}
                setIsOpen={handleModalUpdate}
                initForm={selectUser}
            />
        </>
    );
};

export default DiemDanh;
