import {
  collection,
  doc,
  getDocs,
  query,
  setDoc,
  where,
} from "firebase/firestore";
import moment from "moment";
import { useCallback, useEffect, useRef, useState } from "react";
import { v4 as uuidv4 } from "uuid";

import Scanner from "./components/scanner";
import { db } from "./firebase";

import chaomung from "./assets/checkin.png";
import titlechaomung from "./assets/titlecheckin.png";

function App() {
  const [userCurrent, setUserCurrent] = useState(null);
  const prev = useRef("");

  const scan = useCallback(async (value) => {
    if (value === prev.current) return; // Prevent duplicate scans
    prev.current = value;

    try {
      const userQuery = query(
        collection(db, "users"),
        where("qrcode", "==", value || "")
      );
      const querySnapshot = await getDocs(userQuery);

      if (querySnapshot.empty) {
        console.error("No user found for this QR code");
        return; // Handle case where no user is found
      }

      querySnapshot.forEach(async (_doc) => {
        const date = moment().valueOf();
        setUserCurrent({ ..._doc.data(), checkIn: date });

        const dateString = moment(date).format("DD-MM-YYYY").toString();
        const checkInQuery = query(
          collection(db, "checkIns_test_5"),
          where("qrcode", "==", _doc.data().qrcode)
        );

        const checkInSnapshot = await getDocs(checkInQuery);
        const checkExist = checkInSnapshot.docs.some(snap =>
          dateString === moment(snap.data().checkIn).format("DD-MM-YYYY").toString()
        );

        if (!checkExist) {
          await setDoc(doc(db, "checkIns_test_5", uuidv4()), {
            ..._doc.data(),
            checkIn: date,
          });
        } else {
          console.log("User has already checked in today.");
        }
      });
    } catch (error) {
      console.error("Error during check-in process:", error);
    }
  }, []);

  useEffect(() => {
    const handleSuccess = (localMediaStream) => {
      console.log(localMediaStream);
      // Add logic to handle the media stream (e.g., attach it to a video element)
    };

    const handleError = (err) => {
      alert("Error accessing camera: " + err);
    };

    if (navigator.mediaDevices.getUserMedia) {
      navigator.mediaDevices.getUserMedia({ video: true })
        .then(handleSuccess)
        .catch(handleError);
    } else {
      alert("Sorry, browser does not support camera access");
    }

    return () => {
      // Cleanup logic if necessary (e.g., stopping the media stream)
    };
  }, []);

  return (
    <div className="main-wrapper position-relative">
      <div className="h-100 d-flex flex-column">
        <div className="w-100 d-flex justify-content-center align-items-center" style={{ marginBottom: "5.5vh", marginTop: "28vh" }}>
          <div className="d-flex flex-column" style={{ width: "40%" }}>
            <Scanner onScan={scan} />
          </div>
          <div className="d-flex align-items-center" style={{ width: "40%", maxHeight: "72vh", padding: "1vh 1vw", overflow: "auto", marginLeft: "5.5vw" }}>
            <div className="d-flex flex-column align-items-center">
              <div style={{ maxWidth: "100%" }}>
                <img src={titlechaomung} style={{ width: "100%" }} alt="Welcome Title" />
              </div>
              <h2 className="font-large text-center text-blue" style={{ textTransform: "uppercase", marginBottom: "1.15vh", fontSize: "3.5vw", lineHeight: 1.2 }}>
                {userCurrent?.nguoidaidien || "."}
              </h2>
              <h2 className="font-large text-center text-blue" style={{ textTransform: "uppercase", marginBottom: "1.15vh", fontSize: "2.15vw", lineHeight: 1.2 }}>
                {userCurrent?.chucvu} {userCurrent?.tencongty || "."}
              </h2>
              <div style={{ maxWidth: "100%", marginTop: "1vh" }}>
                <img src={chaomung} style={{ width: "100%" }} alt="Check-in Illustration" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
