import { Modal } from "antd";
import { doc, setDoc } from "firebase/firestore";
import React, { memo, useState } from "react";
import { toast } from "react-toastify";
import { v4 as uuidv4 } from "uuid";
import { db } from "../firebase";

const initState = {
  qrcode: "",
  hovaten: "",
  donvi: "",
  sodienthoai: "",
  room: "",
};

const CreateUserModal = ({ modalIsOpen, setIsOpen }) => {
  const [form, setForm] = useState(initState);

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(form);
    const usersRef = doc(db, "daibieu", uuidv4());
    if (!form.hovaten || form.hovaten === "") {
      form.hovaten = "Họ và tên Đại biểu";
    }
    for (let [key, val] of Object.entries(form)) {
      if (!val || val === "") {
        delete form[key];
      }
    }
    setDoc(usersRef, form);
    setForm(initState);
    toast.success("Thêm mới thành công !", {
      position: "top-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  };

  return (
    <Modal
      title="Đăng ký tham gia Hội nghị"
      centered
      open={modalIsOpen}
      onCancel={() => setIsOpen(false)}
      footer={[]}
    >
      <div>
        <form onSubmit={handleSubmit}>
          <p className="mb-0 mt-2">Họ và tên </p>
          <input
            className="form-control"
            type="text"
            placeholder="Họ và tên"
            onChange={handleChange}
            name="hovaten"
            value={form?.hovaten}
          />
          <p className="mb-0 mt-2">Đơn vị </p>
          <input
            className="form-control"
            type="text"
            required
            onChange={handleChange}
            name="donvi"
            value={form?.donvi}
          />

          <p className="mb-0 mt-2">Đăng ký </p>
          <input
            className="form-control"
            type="text"
            name="room"
            value={form?.room}
            onChange={handleChange}
          />

          <div
            className="d-flex align-items-center"
            style={{
              marginTop: "1rem",

              justifyContent: "flex-end",
            }}
          >
            <button type="submit" className="btn btn-primary text-white">
              Thêm mới
            </button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default memo(CreateUserModal);
