import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // For navigation

function App() {
  const [showPopup, setShowPopup] = useState(true); // State to control popup visibility
  const history = useHistory(); // For navigation

  const handlePopupResponse = (response) => {
    setShowPopup(false); // Close the popup
    if (response === "A") {
      history.push("/danhsach"); // Navigate to danh sách
    } else {
      history.push("/dangky"); // Navigate to đăng ký
    }
  };

  return (
    <div className="main-wrapper position-relative">
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup-content">
            <h2>Chào mừng bạn đến với:</h2>
            <p>Bạn đã đăng ký tham gia hay chưa?</p>
            <button onClick={() => handlePopupResponse("A")}>A. Đã có</button>
            <button onClick={() => handlePopupResponse("B")}>B. Chưa</button>
          </div>
        </div>
      )}
      <div className="h-100 d-flex flex-column">
        <div className="w-100 d-flex justify-content-center align-items-center" style={{ marginBottom: "5.5vh", marginTop: "28vh" }}>
          <h1 className="text-center">Chúc mừng bạn đã đến với trang web của chúng tôi!</h1>
        </div>
      </div>

      <style jsx>{`
        .popup-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: rgba(0, 0, 0, 0.7);
          display: flex;
          justify-content: center;
          align-items: center;
          z-index: 1000; /* Ensure it's above other content */
        }

        .popup-content {
          background: white;
          padding: 20px;
          border-radius: 8px;
          text-align: center;
        }

        .popup-content h2 {
          margin-bottom: 10px;
        }

        .popup-content button {
          margin: 5px;
          padding: 10px 20px;
          border: none;
          border-radius: 5px;
          background-color: #007bff;
          color: white;
          cursor: pointer;
        }

        .popup-content button:hover {
          background-color: #0056b3;
        }
      `}</style>
    </div>
  );
}

export default App;
